package sec03.exam01;

public class BooleanExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean stop = true;
		if(stop) {
			// stop이 true를 가지고 있기때문에 참인 "중지합니다"가 출력됨
			System.out.println("중지합니다");
		}else {
			System.out.println("시작합니다");
		}
	}

}
